const { Client } = require('pg');
const client = new Client();
client.connect();
app.post('/users', (req, res) => {
  const { name, age } = req.body;
  client.query('INSERT INTO users (name, age) VALUES ($1, $2)', [name, age], (err) => {
    if (err) throw err;
    res.send('User added');
  });
});
